import { Badge } from "@/components/ui/badge";
import { AnimatedTable, ColumnDef } from "@/registry/default/ui/animated-table";
import { Avatar, AvatarFallback, AvatarImage } from "@/components/ui/avatar";
import * as React from "react";

interface User {
  id: number;
  name: string;
  email: string;
  role: string;
  status: "active" | "inactive";
  avatar?: string;
  lastLogin: string;
}

const sampleData: User[] = [
  {
    id: 1,
    name: "John Doe",
    email: "john@example.com",
    role: "Admin",
    status: "active",
    avatar: "https://github.com/shadcn.png",
    lastLogin: "2024-01-15",
  },
  {
    id: 2,
    name: "Jane Smith",
    email: "jane@example.com",
    role: "User",
    status: "active",
    avatar: "https://github.com/vercel.png",
    lastLogin: "2024-01-14",
  },
  {
    id: 3,
    name: "Bob Johnson",
    email: "bob@example.com",
    role: "Moderator",
    status: "inactive",
    lastLogin: "2024-01-10",
  },
  {
    id: 4,
    name: "Alice Brown",
    email: "alice@example.com",
    role: "User",
    status: "active",
    lastLogin: "2024-01-13",
  },
  {
    id: 5,
    name: "Charlie Wilson",
    email: "charlie@example.com",
    role: "Admin",
    status: "active",
    avatar: "https://github.com/timneutkens.png",
    lastLogin: "2024-01-12",
  },
];

const columns: ColumnDef<User>[] = [
  {
    id: "name",
    header: "Name",
    accessorKey: "name",
    sortable: true,
    cell: (user) => (
      <div className="flex items-center gap-3">
        <Avatar className="h-8 w-8">
          <AvatarImage src={user.avatar} alt={user.name} />
          <AvatarFallback>
            {user.name
              .split(" ")
              .map((n) => n[0])
              .join("")}
          </AvatarFallback>
        </Avatar>
        <div>
          <div className="font-medium">{user.name}</div>
          <div className="text-sm text-muted-foreground">{user.email}</div>
        </div>
      </div>
    ),
  },
  {
    id: "role",
    header: "Role",
    accessorKey: "role",
    sortable: true,
    cell: (row) => (
      <Badge variant={row.role === "Admin" ? "default" : "secondary"}>
        {row.role}
      </Badge>
    ),
  },
  {
    id: "status",
    header: "Status",
    accessorKey: "status",
    sortable: true,
    cell: (row) => (
      <Badge variant={row.status === "active" ? "default" : "destructive"}>
        {row.status}
      </Badge>
    ),
  },
  {
    id: "lastLogin",
    header: "Last Login",
    accessorKey: "lastLogin",
    sortable: true,
  },
];

export default function AnimatedTableDemo() {
  const [selectedIds, setSelectedIds] = React.useState<(string | number)[]>([]);
  const [sortColumn, setSortColumn] = React.useState<string>("");
  const [sortDirection, setSortDirection] = React.useState<"asc" | "desc" | null>(null);
  const [searchValue, setSearchValue] = React.useState("");

  // Filter and sort data
  const filteredData = React.useMemo(() => {
    let filtered = sampleData;

    // Apply search filter
    if (searchValue) {
      filtered = filtered.filter(
        (user) =>
          user.name.toLowerCase().includes(searchValue.toLowerCase()) ||
          user.email.toLowerCase().includes(searchValue.toLowerCase()) ||
          user.role.toLowerCase().includes(searchValue.toLowerCase())
      );
    }

    // Apply sorting
    if (sortColumn && sortDirection) {
      filtered = [...filtered].sort((a, b) => {
        const aValue = a[sortColumn as keyof User];
        const bValue = b[sortColumn as keyof User];

        if (aValue < bValue) return sortDirection === "asc" ? -1 : 1;
        if (aValue > bValue) return sortDirection === "asc" ? 1 : -1;
        return 0;
      });
    }

    return filtered;
  }, [searchValue, sortColumn, sortDirection]);

  const handleSort = (columnId: string, direction: "asc" | "desc" | null) => {
    setSortColumn(columnId);
    setSortDirection(direction);
  };

  const handleSelectionChange = (ids: (string | number)[]) => {
    setSelectedIds(ids);
  };

  return (
    <div className="flex flex-col gap-3">
      <h3 className="font-medium text-sm">Animated Table</h3>
      <AnimatedTable
        data={filteredData}
        columns={columns}
        selectable
        selectedIds={selectedIds}
        onSelectionChange={handleSelectionChange}
        sortColumn={sortColumn}
        sortDirection={sortDirection}
        onSort={handleSort}
        searchable
        searchValue={searchValue}
        onSearchChange={setSearchValue}
        searchPlaceholder="Search users..."
        columnVisibility
        expandable
        renderExpandedRow={(row) => (
          <div className="space-y-2">
            <h4 className="font-medium">User Details</h4>
            <p className="text-sm text-muted-foreground">
              Full details for {row.name} would go here.
            </p>
          </div>
        )}
        striped
        stickyHeader
      />
    </div>
  );
}
